(ns my-first
  (:require [gorilla-plot.core :as plot]
            [gorilla-repl.table :as table]
            [gorilla-repl.html :as html]
            [gorilla-repl.latex :as latex]))

;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-nil'>nil</span>","value":"nil"}
;; <=

;; @@
(def n 100) ; number of values to be considered
(def ymax 100) ; maximum y value 

(def xvals (range 1 (inc n))) ; x vals as step of 1 
(def yvals (take n (repeatedly #(rand-int (inc ymax))))) ; take n y vals 
														 ; as repeated invocation of 
                                                         ; rand-int returning 0 to ymax
(def data (map list xvals yvals)) ; combination of x and y as list of pairs
